import React, { useState } from "react";
import axios from "axios";

const Weather: React.FC = () => {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState<any>(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const fetchWeather = async () => {
    if (!city.trim()) {
      setError("Please enter a city name");
      setWeather(null);
      return;
    }
    setLoading(true);
    try {
      const res = await axios.get(`http://127.0.0.1:8000/weather?city=${encodeURIComponent(city)}`);
      const data = res.data as { error?: string } & Record<string, unknown>;
      if (data && !data.error) {
        setWeather(data);
        setError("");
      } else {
        setError(data?.error || "City not found");
        setWeather(null);
      }
    } catch {
      setError("Could not fetch weather");
      setWeather(null);
    } finally {
      setLoading(false);
    }
  };

  const onKeyDown: React.KeyboardEventHandler<HTMLInputElement> = (e) => {
    if (e.key === "Enter") fetchWeather();
  };

  const temperature = weather?.main?.temp;
  const description = weather?.weather?.[0]?.description ?? "";
  const icon = weather?.weather?.[0]?.icon ?? ""; // e.g., 10d
  const cityName = weather?.name ?? "";
  const country = weather?.sys?.country ?? "";
  const wind = weather?.wind?.speed;
  const humidity = weather?.main?.humidity;
  const iconUrl = icon ? `https://openweathermap.org/img/wn/${icon}@2x.png` : "";

  return (
    <div className="container" style={{ width: "100%" }}>
      <div style={{
        background: "rgba(255, 255, 255, 0.9)",
        backdropFilter: "blur(6px)",
        borderRadius: 16,
        boxShadow: "0 10px 25px rgba(0,0,0,0.15)",
        padding: 24,
        margin: "2rem auto",
        maxWidth: 720
      }}>
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 16 }}>
          <h1 style={{ fontSize: 28, fontWeight: 700 }}>Weather</h1>
          <div style={{ display: "flex", gap: 8 }}>
            <input
              type="text"
              value={city}
              onChange={(e) => setCity(e.target.value)}
              onKeyDown={onKeyDown}
              placeholder="Search city..."
              aria-label="City name"
              style={{
                padding: "10px 12px",
                borderRadius: 10,
                border: "1px solid #cbd5e1",
                outline: "none",
                minWidth: 220
              }}
            />
            <button
              onClick={fetchWeather}
              disabled={loading}
              style={{
                background: "#0ea5e9",
                color: "#fff",
                padding: "10px 14px",
                border: 0,
                borderRadius: 10,
                fontWeight: 600,
                cursor: loading ? "not-allowed" : "pointer",
                opacity: loading ? 0.7 : 1
              }}
            >
              {loading ? "Loading..." : "Get Weather"}
            </button>
          </div>
        </div>

        {error && (
          <div style={{
            background: "#fee2e2",
            color: "#b91c1c",
            border: "1px solid #fecaca",
            padding: 12,
            borderRadius: 12,
            marginBottom: 16
          }}>
            {error}
          </div>
        )}

        {weather && (
          <div style={{ display: "grid", gridTemplateColumns: "1fr", gap: 16 }}>
            {/* Current conditions card */}
            <div style={{
              background: "#ffffff",
              border: "1px solid #e2e8f0",
              borderRadius: 14,
              padding: 20,
            }}>
              <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between", gap: 16 }}>
                <div>
                  <h2 style={{ fontSize: 24, fontWeight: 700 }}>{cityName}{country ? `, ${country}` : ""}</h2>
                  <p style={{ textTransform: "capitalize", color: "#334155" }}>{description}</p>
                  <div style={{ display: "flex", alignItems: "baseline", gap: 8 }}>
                    <span style={{ fontSize: 48, fontWeight: 700 }}>{Math.round(temperature)}°C</span>
                  </div>
                </div>
                {iconUrl && (
                  <img src={iconUrl} alt={description} width={100} height={100} style={{ imageRendering: "-webkit-optimize-contrast" }} />
                )}
              </div>

              <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: 12, marginTop: 16 }}>
                <div style={{
                  background: "#f8fafc",
                  border: "1px solid #e2e8f0",
                  borderRadius: 12,
                  padding: 12,
                  textAlign: "center"
                }}>
                  <p style={{ color: "#64748b" }}>Humidity</p>
                  <p style={{ fontSize: 20, fontWeight: 700 }}>{humidity ?? "-"}%</p>
                </div>
                <div style={{
                  background: "#f8fafc",
                  border: "1px solid #e2e8f0",
                  borderRadius: 12,
                  padding: 12,
                  textAlign: "center"
                }}>
                  <p style={{ color: "#64748b" }}>Wind</p>
                  <p style={{ fontSize: 20, fontWeight: 700 }}>{wind ?? "-"} m/s</p>
                </div>
                <div style={{
                  background: "#f8fafc",
                  border: "1px solid #e2e8f0",
                  borderRadius: 12,
                  padding: 12,
                  textAlign: "center"
                }}>
                  <p style={{ color: "#64748b" }}>Feels Like</p>
                  <p style={{ fontSize: 20, fontWeight: 700 }}>{weather?.main?.feels_like ? Math.round(weather.main.feels_like) : "-"}°C</p>
                </div>
              </div>
            </div>

            {/* Placeholder for forecast grid (future expansion) */}
            <div style={{
              background: "#ffffff",
              border: "1px solid #e2e8f0",
              borderRadius: 14,
              padding: 20
            }}>
              <h3 style={{ fontSize: 18, fontWeight: 700, marginBottom: 8 }}>Forecast</h3>
              <p style={{ color: "#64748b" }}>Forecast data coming soon.</p>
            </div>
          </div>
        )}

        {!weather && !error && !loading && (
          <div style={{
            background: "#ffffff",
            border: "1px solid #e2e8f0",
            borderRadius: 14,
            padding: 20,
            textAlign: "center"
          }}>
            <p style={{ color: "#64748b" }}>Search for a city to see current conditions.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Weather;
